                new Specialty { Id=new Guid("3bc58596-559f-4e09-a4a7-2696bfddc1ed"), Title="Разработчик нейропротезов", Description="Разработка бионических протезов и имплантов - мультидисциплинарная область, требующая совместной работы специалистов разного профиля, тем не менее это одна из профессий будущего, которая действительно может изменить жизнь многих людей."},
                new Specialty { Id=new Guid("d47b7c2c-747a-4c31-affb-c92c5df1e302"), Title="Реабилитолог", Description="Реабилитолог – врач с высшим медицинским образованием, занимающийся восстановлением здоровья пациентов. Обладает физической силой и выносливостью, высоким уровнем эмпатии. Профессия подходит для людей любого возраста."},
                new Specialty { Id=new Guid("6e729f55-7b72-419f-94f3-458f05684e95"), Title="Ревматолог", Description="Часто встречающиеся болезни костно-мышечной системы лечит именно ревматолог. Для того чтобы стать ревматологом, необходимо получить высшее образование, быть ответственным и сострадательным."},
                new Specialty { Id=new Guid("08489843-c17c-4e27-83db-052b9b9bc45e"), Title="Рентгенолог", Description="Рентгенолог специализируется на лучевой диагностике, однако не занимается лечением, а только консультирует пациентов исходя из полученных снимков."},
                new Specialty { Id=new Guid("9042b0fd-fdfc-4fec-b0ad-e1d31eed7f6a"), Title="Санитар", Description="Это морально устойчивый человек, осознающий, с какой работой ему придется иметь дело."},
                new Specialty { Id=new Guid("0807d1aa-002f-4ac2-9c5a-83da810548fd"), Title="Сиделка", Description="Сиделка – универсальный специалист, в котором объединились качества хорошего психолога, опытной медсестры и заботливой компаньонки. Сиделки являются высоко востребованными специалистами, имеют достойный уровень дохода."},
                new Specialty { Id=new Guid("ad17d122-685e-456e-aef7-ac9d972b9933"), Title="Специалист по безопасности лекарственных средств", Description="Все лекарства в той или иной степени обладают побочными эффектами. Некоторые непредусмотренные реакции являются причинами смерти людей. Данная профессия необходима для предупреждения использования небезопасных лекарств."},
                new Specialty { Id=new Guid("5134d4c2-e104-4bee-8bf5-ef877b3bea02"), Title="Старшая медицинская сестра", Description="Старшая медсестра — специалист со средним медицинским образованием. На эту должность назначается, как правило, опытная медицинская сестра с практическим стажем работы не менее 5 лет. Круг обязанностей старшей медсестры очень широк, на ней практически держится работа всего отделения. ✓ Мы расскажем вам о том, где работает старшая медсестра, что она делает, сколько зарабатывает и о том, как ею стать."},
                new Specialty { Id=new Guid("f17c5dab-c804-4d97-bad3-0d1733a4686c"), Title="Стоматолог", Description="Стоматолог общего профиля занимается лечением болезней и повреждений зубов, челюстей и т.п. Однако есть и более узкие востребованные направления, такие как: пародонтология, хирургическая стоматология, ортодонтия и другие."},
                new Specialty { Id=new Guid("ab3113d7-ff68-4b3e-9cc8-9b010c8ba6d5"), Title="Стоматологический гигиенист", Description="Гигиенист-стоматолог должен быть очень честным, тактичным и увлеченным человеком, который в любое время готов прийти на помощь своим пациентам."},
                new Specialty { Id=new Guid("9c87f7b6-7e8c-46fe-a9da-ae26e1c7efc3"), Title="Сурдолог", Description="Сурдолог занимается лечением нарушений и заболеваний органов слуха в том случае, если снижение слуха стойкое и не поддается лечению, назначенному ЛОР-врачом."},
                new Specialty { Id=new Guid("6b2d9093-157e-46e7-b298-f4ec052e43b0"), Title="Терапевт", Description="Врачи-терапевты – незаменимые медицинские работники, которые спасают жизни, пропагандируют пользу ранней диагностики, занимаются научной деятельностью. Это неудивительно, ведь терапия - царица наук!"},
                new Specialty { Id=new Guid("0d0fa827-3d5b-43bc-80d9-d2fe886e8e6b"), Title="Тканевый инженер", Description="Тканевая инженерия - новое и важнейшее направление современной медицины, так как в перспективе оно позволит разрешить ряд существующих проблем трансплантологии, одна из которых — серьезная нехватка донорских органов."},
                new Specialty { Id=new Guid("81d70e2f-ca14-4d1f-b038-a0a6f4a3868a"), Title="Торакальный хирург", Description="Торакальный хирург проводит операции для лечения заболеваний, травм и разрывов органов грудной клетки и средостения. Для работы торакальным хирургом необходимо получить высшее медицинское образование."},
                new Specialty { Id=new Guid("01b38368-201c-4a63-8ff3-88aa9c053f28"), Title="Травматолог (Травматолог-ортопед)", Description="За лечением опорно-двигательного аппарата с каждым годом обращается всё больше пациентов, что говорит о востребованности врачей ортопедов и травматологов и обеспечивает высокий уровень оплаты труда."},
                new Specialty { Id=new Guid("5e74ed67-fc35-45f6-a854-1fbdc30489f0"), Title="Уролог", Description="Профессию уролог можно получить в высших медицинских учреждениях, обучаясь в течение 8 лет (6 – специалитет, 2 – ординатура). Она подойдет для целеустремленных, ответственных абитуриентов, стремящихся помогать людям."},
                new Specialty { Id=new Guid("a1a475c6-5044-4244-904d-49abaac485ab"), Title="Фармаколог", Description="Фармаколог должен обладать повышенной концентрацией внимания, точностью, хорошей памятью, человеколюбием, склонностью к монотонной научной работе."},
                new Specialty { Id=new Guid("e59b2bd0-f840-499f-8c45-3fb160d8f51b"), Title="Фельдшер", Description="Анализирует результаты обследований, выполняет лечебные манипуляции, направляет людей к узким специалистам."},
                new Specialty { Id=new Guid("c96a90e8-c17c-441c-abf2-585b9372ddb7"), Title="Физиотерапевт", Description="Физиотерапия – это раздел медицины, посвященный лечебным воздействиям физических факторов, таких как тепло, холод, электрический ток, магнитные поля, ультразвук, лазер и пр. Для работы необходимо получить высшее образование."},
                new Specialty { Id=new Guid("5eeb4cef-2c7c-4633-9e5a-e45415410409"), Title="Фониатр", Description="Фониатр лечит пациентов, имеющих заболевания или проблемы с голосом или голосовыми связками. Специфика данной медицинской профессии - возможность работать в театрах и филармониях."},
                new Specialty { Id=new Guid("3ff1998d-f699-45ff-9ff8-ebe826381958"), Title="Фтизиатр", Description="Фтизиатрия является разделом медицины, изучающим туберкулез. Врач-фтизиатр занимается диагностикой, профилактикой и лечением этого заболевания, которое эксперты называют чумой XXI века. Работа непростая и опасная, требует самоотдачи."},
                new Specialty { Id=new Guid("57caf01e-0156-4646-806b-58f68ce9941f"), Title="Хирург", Description="Хирург престижная медицинская специальность, связанная преимущественно с оперативным лечением. Хирурги проводят операции, ставят диагнозы, контролируют процесс реабилитации — выполняют важную, сложную и очень ответственную работу."},
                new Specialty { Id=new Guid("685e336c-5439-4e90-a0b4-e29b506e2d47"), Title="Хирург-ветеринар", Description="У хирурга-ветеринара должна быть отлично развита моторика, важно хорошее зрение, отсутствие проблем с опорно-двигательным аппаратом, ведь большую часть рабочего времени приходится проводить на ногах."},
                new Specialty { Id=new Guid("e2cf40b3-d831-4f06-b7e9-7c659cdd080e"), Title="Хирург-гинеколог", Description="Хирургическая гинекология является очень важной отраслью медицины, а врачи, которые выбирают это направление, всегда востребованы."},
                new Specialty { Id=new Guid("d8004ae3-b3f5-4aa5-a687-1aec55444982"), Title="Хирург-колопроктолог", Description="В их характере преобладает сострадание, терпеливость, ответственность, они отличаются высокой точностью, упорством, не боятся форс-мажоров."},
                new Specialty { Id=new Guid("fef48046-db68-4866-bea7-16ecbe162155"), Title="Хирург-онколог", Description="Хирург-онколог — одна из самых сложных и престижных медицинских профессий."},
                new Specialty { Id=new Guid("5672a00e-fb1d-4012-b9d5-4b8f869c1ffa"), Title="Хирург-ортопед", Description="Ежедневно хирург-ортопед работает с пациентами, страдающими от сильных травм, сложнейших переломов, поэтому у врача должны быть крепкие нервы и жесткий характер."},
                new Specialty { Id=new Guid("4ea01007-8aed-4f3e-ad3e-78ed874bd8c1"), Title="Хирург-оториноларинголог", Description="Этот врач должен быть ответственным, беспрекословным, грамотным, гуманным, инициативным, аккуратным."},
                new Specialty { Id=new Guid("ba64ce29-ae7b-4b73-b1ca-a412e902cc2b"), Title="Хирург-офтальмолог", Description="Операции на глазах были и остаются ювелирной работой, с которой может справиться не каждый врач."},
                new Specialty { Id=new Guid("1d60b956-ff2a-40f2-8f16-6b74a7c4d8cf"), Title="Хирург-стоматолог", Description="Хирургом-стоматологом могут работать как девушки, так и мужчины, отличающиеся педантичностью, аккуратностью, обязательностью, внимательностью, огромным терпением, чувством такта."},
                new Specialty { Id=new Guid("b1925a0f-de98-40b7-be4a-8b41b932a543"), Title="Хирург-травматолог", Description="Он должен иметь определенный склад характера, в котором преобладает жесткость, умение договариваться с любым человеком, ассертивность, безапелляционность, упорство и настойчивость."},
                new Specialty { Id=new Guid("e35bb958-11e4-49d7-9a44-2a701ea1758d"), Title="Хирург-уролог", Description="Хирург-уролог — это объективный, сосредоточенный человек, который не страдает от нервных заболеваний, болезней костей, вен и суставов, органов зрения."},
                new Specialty { Id=new Guid("18bf3de7-d8b4-4320-ad48-d767e37b4229"), Title="Хирург-эндокринолог", Description="Он должен отличаться моральной устойчивостью, даром убеждения, авторитарностью. Обязан вызывать уважение у пациентов, быть готовым к тому, что не все люди, перенесшие операцию, будут исцелены."},
                new Specialty { Id=new Guid("ef24a575-a10c-44d2-87aa-fe554dada63e"), Title="Художник-глазопротезист", Description="Художник-глазопротезист – специалист по изготовлению глазных протезов."},
                new Specialty { Id=new Guid("0a1bef1b-77c6-46e8-a2f4-0bc074093c94"), Title="Цитолог", Description="Цитолог – врач, работающий в сфере клинической лабораторной диагностики. Проводит исследования, интерпретирует результаты, внедряет инновационные технологии и методы. Срок обучения на цитолога составляет 8 лет."},
                new Specialty { Id=new Guid("03d07bce-3b1b-420c-8a85-99df3b65b732"), Title="Челюстно-лицевой хирург", Description="Как правило, к челюстно-лицевому хирургу пациенты поступают с переломами костей лица, врождёнными проблемами, опухолями и пр. И он не только восстанавливает повреждённые функции и физическое здоровье, но и возвращает человеку утраченную красоту."},
                new Specialty { Id=new Guid("93fd5c68-c5c8-4a9c-80ec-e14bd57b787f"), Title="Эмбриолог", Description="Эмбриолог – специалист, который является ключевым звеном репродуктивной медицины. Он имеет высшее медицинское или биологическое образование, нередко совмещает практическую работу с научной и преподавательской."},
                new Specialty { Id=new Guid("bc9d8bc1-f7e9-49f2-a655-d7ccea38fc28"), Title="Эндокринолог", Description="Эндокринолог отвечает за своевременное выявление и лечение гормональных сбоев и гормонального дисбаланса, которые могут повлечь серьезные проблемы здоровья пациентов. Для работы по этой профессии необходимо получить высшее образование."},
                new Specialty { Id=new Guid("d6efca03-30ed-45fc-a98b-aa1cfac18c15"), Title="Эфферентолог", Description="Эфферентолог – специалист, который очищает кровь и организм с применением эфферентных методов лечения. Имеет высшее медицинское образование, постоянно обучается и обладает развитой эмпатией."},
            };
    }
}
